import React from 'react';
import API_KEY from './api-key';

export default class Movies extends React.Component {
    state = {
        queryValue: '',
        results: [],
        resultsLoading: true
    };

    searchForm = () => {
        return this.state.criticsLoading
        ? <div className='searchFormLoading'>Loading critics...</div>
        : <div>
            <h2>Select a critic in the form below:</h2>
            <form onSubmit={this.searchQuery}>
                <label htmlFor="name">Name: </label>
                <select id="name" name="name" onChange={this.queryCriticEntryChange}>
                    <option value=''>Select one</option>
                    {this.state.critics.results.map(result =>
                        <option key={result.display_name} value={result.seo_name}>{result.display_name}</option>
                    )}
                </select>
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    }

    queryValueEntryChange = (e) => {
        this.setState({ queryValue: e.target.value });
    }

    searchQuery = (e) => {
        e.preventDefault();
        fetch(`https://api.nytimes.com/svc/movies/v2/reviews/search.json?query=lebowski&api-key=${API_KEY}`)
        .then(res => res.json())
        .then(data => this.setState({
          results: data,
          resultsLoading: false
        }));
    }

    searchResults = () => {
        return this.state.resultsLoading
        ? <div className='searchResultsLoading'>Please complete the search above to display a list of results.</div>
        : <div>
            <h2 className='page-sub-heading'>Results:</h2>
            <div className='search-results'>
                Results go here!
            </div>
        </div>

    }

    render() {
        return(
            <div>
                <h1 className='page-heading'>Movies</h1>
                <h2>Add a keyword below:</h2>
                <form onSubmit={this.searchQuery}>
                    <label htmlFor="keyword">Keyword: </label>
                    <input
                        type="text"
                        id="keyword"
                        name="keyword"
                        placeholder="election"
                        value={this.state.entry}
                        onChange={this.queryValueEntryChange}
                    />
                    <br/>
                    <input type="submit" value="Submit" />
                </form>
                <div>{this.searchResults()}</div>
            </div>
        )
    }
}
