import React from 'react';
import API_KEY from './api-key';

export default class BestSellerSearch extends React.Component {
    state = {
        genres: [],
        genresLoading: true
    };

    componentDidMount() {
        // Need to replace API key with parameter!!!
        fetch(`https://api.nytimes.com/svc/books/v3//lists/names.json?api-key=${API_KEY}`)
        .then(res => res.json())
        .then(data => this.setState({
          genres: data,
          genresLoading: false
        }));
    }

    searchForm = () => {
        return this.state.genresLoading
        ? <div className='searchFormLoading'>Loading genres...</div>
        : <div>
            <h2>Input a date and select a genre in the form below:</h2>
            <form>
                <label htmlFor="date">Date:</label>
                <input type="text" id="date" name="date" placeholder="2020-02-20"/>
                <br/>
                <label htmlFor="genre">Genre:</label>
                <select id="genre" name="genre">
                    {this.state.genres.results.map(result =>
                        <option key={result.list_name} value={result.list_name_encoded}>{result.list_name}</option>
                    )}
                </select>
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    }

    render(){
        return(
            <div>
                <h1 className='page-heading'>Bestsellers</h1>
                <div>{this.searchForm()}</div>
            </div>
        )
    }
}
