import React from 'react';
// import API_KEY from './api-key';

export default class BestSellerSearch extends React.Component {
    state = {
        genres: [],
    }

    componentDidMount() {
        // Need to replace API key with parameter!!!
        // fetch(`https://api.nytimes.com/svc/books/v3//lists/names.json?api-key=${API_KEY}`)
        fetch(`https://api.nytimes.com/svc/books/v3//lists/names.json?api-key=COCHMynqLfj4ddpahmMAHUNGJ3pOqOfP`)
        .then(res => res.json())
        .then(data => this.setState({
          details: data,
          isLoading: false
        }));
    }

    render(){
        return(
            <div>
                <h1 className='page-heading'>Bestsellers</h1>
                <h2>Select a date and genre in the form below</h2>
                <form>
                    <label for="date">Date:</label>
                    <input type="text" id="date" name="date" placeholder="2020-02-20"/>
                    <br/>
                    <label for="genre">Genre:</label>
                    <select id="genre" name="genre">
                        <option value="" selected disabled hidden>Select one</option>
                        <option value="a">Volvo</option>
                        {/* map over /lists/names.json response and create options */}
                    </select>
                </form>
            </div>
        )
    }
}
