import React from 'react';
import API_KEY from './api-key';

export default class Movies extends React.Component {
    state = {
        critics: [],
        criticsLoading: true,
        results: [],
        resultsLoading: true
    };

    componentDidMount() {
        fetch(`https://api.nytimes.com/svc/movies/v2/critics/all.json?api-key=${API_KEY}`)
        .then(res => res.json())
        .then(data => this.setState({
            critics: data,
            criticsLoading: false
        }));
    }

    searchForm = () => {
        return this.state.criticsLoading
        ? <div className='searchFormLoading'>Loading critics...</div>
        : <div>
            <h2>Select a critic in the form below:</h2>
            <form>
                <label htmlFor="name">Name: </label>
                <select id="name" name="name">
                    {this.state.critics.results.map(result =>
                        <option key={result.display_name} value={result.seo_name}>{result.display_name}</option>
                    )}
                </select>
                <br/>
                <input type="submit" value="Submit" />
            </form>
        </div>
    }

    searchResults = () => {
        return this.state.resultsLoading
        ? <div className='searchResultsLoading'>Please complete the search above to display a list of results.</div>
        : <div>
            <h2 className='page-sub-heading'>Results:</h2>
        </div>

    }

    render() {
        return(
            <div>
                <h1 className='page-heading'>Movies</h1>
                <div>{this.searchForm()}</div>
                <div>{this.searchResults()}</div>
            </div>
        )
    }
}
